                        xstrategyName:="CREDIT CLASS UPGRADE";
                    //    xstrategyVersion:=xReleaseVersion+".001";
            else if(DMFunction = "BCC" ) then
                        xstrategyName:="BUSINESS CREDIT CLASS";
                       // xstrategyVersion:=xReleaseVersion+".001";
            else if(DMFunction = "UWR" ) then
                        xstrategyName:="UNDERWRITER REVIEW";
                    //    xstrategyVersion:=xReleaseVersion+".001"; 
