             (InNumberList(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_MMOPA, "7, 8, 9", ",") ) OR
                (iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE <> null AND InStringList(TRIM(iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE), "AM, AS, BCD, BDI, BDM, BKC, BKD, BKL, BKW, BRC, BRR, CBL, CBR, CBT, CCA, CCD, CDC, CDD, CDL, CDR, CDT, CED, CHK, CLS, CPP, CRC, CRD, CRL, CRR, CRT, CRV, CTC, CTV, DLF, DLU, ET, ETA, ETB, ETD, FCL, FFB, FPD, FPI, FPS, FRD, FTB, GOF, GVC, IRB, IRE, IRO, JUD, JUG, LBR, MCC, MOV, PBB, PDD, PFC, PNR, PLP, PNR, PPA, PPC, PPD, PPL, PRL, PTD, PWG, REA, RLD, RLP, RPD, RPF, RPO, RRE, RVC, RVD, RVE, RVN, RVP, RVR, SET, SK, SLD, SLP, SRG, STU, TTR", ","))) AND 
                          iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_COLLECTION = false) then
            iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_WE_DRGNOCOL := 1;
 endif


 //WE-90NOCOL
 iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_WE_90NOCOL := 0;
 if(((InNumberList(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_CMOPA, "4, 5, 7, 8, 9", ",")) OR 
             (InNumberList(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_MMOPA, "4, 5, 7, 8, 9", ",")) OR
             (iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE <> null AND InStringList(TRIM(iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE), "AM, AS, BCD, BDI, BDM, BKC, BKD, BKL, BKW, BRC, BRR, CBL, CBR, CBT, CCA, CCD, CDC, CDD, CDL, CDR, CDT, CED, CHK, CLS, CPP, CRC, CRD, CRL, CRR, CRT, CRV, CTC, CTV, DLF, DLU, ET, ETA, ETB, ETD, FCL, FFB, FPD, FPI, FPS, FRD, FTB, GOF, GVC, IRB, IRE, IRO, JUD, JUG, LBR, MCC, MOV, PBB, PDD, PFC, PNR, PLP, PNR, PPA, PPC, PPD, PPL, PRL, PTD, PWG, REA, RLD, RLP, RPD, RPF, RPO, RRE, RVC, RVD, RVE, RVN, RVP, RVR, SET, SK, SLD, SLP, SRG, STU, TTR", ",")) OR
             (CharCount("4", iNORMALIZED_BUREAU_SEGMENTS.PAY_PATT_INTERPRETED) > 0 AND CharCount("5", iNORMALIZED_BUREAU_SEGMENTS.PAY_PATT_INTERPRETED) > 0) OR
             (iNORMALIZED_BUREAU_SEGMENTS.NUM_90_DPD <> null AND VAL(iNORMALIZED_BUREAU_SEGMENTS.NUM_90_DPD) > 0)) AND 
                       iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_COLLECTION = false) then
         iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_WE_90NOCOL := 1;
 endif


 //RATINGS-OF30
 iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_RATINGS_OF30 := CharCount("2", iNORMALIZED_BUREAU_SEGMENTS.PAY_PATT_INTERPRETED);




 //WORST-EVER
 xMaxOfCMOPA_MMOPA := MAX(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_CMOPA, iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_MMOPA);
 xMaxValueInGrid := dm_MinMax_ValueInGrid(iNORMALIZED_BUREAU_SEGMENTS.PAY_PATT_INTERPRETED, "MAX");

 if(InNumberList(xMaxOfCMOPA_MMOPA, "7, 8, 9", ",") OR iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_DEROG) then
       iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_WORST_EVER := 400;
 else if (xMaxOfCMOPA_MMOPA = 5 OR xMaxValueInGrid = "5") then
       iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_WORST_EVER := 120;
 else if (xMaxOfCMOPA_MMOPA = 4 OR (iNORMALIZED_BUREAU_SEGMENTS.NUM_90_DPD <> null AND VAL(iNORMALIZED_BUREAU_SEGMENTS.NUM_90_DPD) > 0) OR xMaxValueInGrid = "4") then
       iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_WORST_EVER := 90;
 else if (xMaxOfCMOPA_MMOPA = 3 OR (iNORMALIZED_BUREAU_SEGMENTS.NUM_60_DPD <> null AND VAL(iNORMALIZED_BUREAU_SEGMENTS.NUM_60_DPD) > 0) OR xMaxValueInGrid = "3") then
       iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_WORST_EVER := 60;
 else if ((xMaxOfCMOPA_MMOPA = 2 OR (iNORMALIZED_BUREAU_SEGMENTS.NUM_30_DPD <> null AND VAL(iNORMALIZED_BUREAU_SEGMENTS.NUM_30_DPD) > 0) OR xMaxValueInGrid = "2") AND
                           ((iNORMALIZED_BUREAU_SEGMENTS.NUM_30_DPD <> null AND VAL(iNORMALIZED_BUREAU_SEGMENTS.NUM_30_DPD) > 3) OR iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_RATINGS_OF30 > 3)) then
       iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_WORST_EVER := 34;
 else if (xMaxOfCMOPA_MMOPA = 2 OR (iNORMALIZED_BUREAU_SEGMENTS.NUM_30_DPD <> null AND VAL(iNORMALIZED_BUREAU_SEGMENTS.NUM_30_DPD) > 0) OR xMaxValueInGrid = "2") then
       iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_WORST_EVER := 30;
 else if (xMaxOfCMOPA_MMOPA = 1) then
       iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_WORST_EVER := 1;
 else
       iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_WORST_EVER := 0;
 endif endif endif endif endif endif endif


 //PRESENT-STATUS
 if( ((InNumberList(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_CMOPA, "7, 8, 9", ",")) OR (iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_COLLECTION) OR (iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_IND_CODE = "Y") ) AND 
                  (iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE <> null AND NOT InStringList(iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE, "CWC, ETO, PCL, PFC, PPD, PPL, RPD, RRE, RVR", ",")) ) then
       iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_PRESENT_STATUS := 400;
 else if (iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_CMOPA = 5) then
       iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_PRESENT_STATUS := 120;
 else if (iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_CMOPA = 4) then
       iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_PRESENT_STATUS := 90;
 else if (iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_CMOPA = 3) then
       iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_PRESENT_STATUS := 60;
 else if ((iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_CMOPA = 2 AND iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_WORST_EVER <= 34) AND ((iNORMALIZED_BUREAU_SEGMENTS.NUM_30_DPD <> null AND VAL(iNORMALIZED_BUREAU_SEGMENTS.NUM_30_DPD) > 3) OR iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_RATINGS_OF30 > 3)) then
       iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_PRESENT_STATUS := 34;
 else if (iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_CMOPA = 2) then
       iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_PRESENT_STATUS := 30;
 else if (iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_CMOPA = 1 OR (iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE <> null AND InStringList(iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE, "CWC, ETO, PCL, PFC, PPD, PPL, RPD, RRE, RVR", ","))) then
       iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_PRESENT_STATUS := 1;
 else
       iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_PRESENT_STATUS := 0;

 endif endif endif endif endif endif endif

xNumber := iNumber;

if(xNumber < 0) then
              xNumber := 0 - xNumber;
endif


