 if((BusinessApplicantOutput.BusChargeOffNum <> null and BusinessApplicantOutput.BusChargeOffNum >= 0) and (BusinessApplicantOutput.BusActiveTradesNum <> null and BusinessApplicantOutput.BusActiveTradesNum > 0))then
           BusinessApplicantOutput.BusPctChargeOffToActiveTrades := BusinessApplicantOutput.BusChargeOffNum/BusinessApplicantOutput.BusActiveTradesNum;
else 
           BusinessApplicantOutput.BusPctChargeOffToActiveTrades := 0;
 endif

if(BusinessApplicantOutput.BusChargeOffNum <> null and BusinessApplicantOutput.BusChargeOffNum <= 2 AND
     BusinessApplicantOutput.BusActiveTradesNum <> null and BusinessApplicantOutput.BusActiveTradesNum>= 4 AND
     BusinessApplicantOutput.BusJudgementLienNum <> null and BusinessApplicantOutput.BusJudgementLienNum <= 2)then
         xBusPass2of3MidHigh := "PASS";
endif

 if(BusinessApplicantOutput.BusChargeOffNum<>null and BusinessApplicantOutput.BusChargeOffNum<=1 AND 
      BusinessApplicantOutput.BusActiveTradesNum<>null and BusinessApplicantOutput.BusActiveTradesNum>=3 AND 
      BusinessApplicantOutput.BusJudgementLienNum<>null and BusinessApplicantOutput.BusJudgementLienNum<=1)then
  xBusPass2of3MidMed:="PASS";       
endif

 if(BusinessApplicantOutput.BusChargeOffNum<>null and BusinessApplicantOutput.BusChargeOffNum<=0 AND 
      BusinessApplicantOutput.BusActiveTradesNum<>null and BusinessApplicantOutput.BusActiveTradesNum>=2 AND
      BusinessApplicantOutput.BusJudgementLienNum<>null and BusinessApplicantOutput.BusJudgementLienNum=0)then
       xBusPass2of3MidLow:="PASS";
endif

  if(BusinessApplicantOutput.BusChargeOffNum<>null and BusinessApplicantOutput.BusChargeOffNum<=0 AND
       BusinessApplicantOutput.BusActiveTradesNum<>null and BusinessApplicantOutput.BusActiveTradesNum>=2 AND
       BusinessApplicantOutput.BusJudgementLienNum<>null and BusinessApplicantOutput.BusJudgementLienNum=0)then
