                (BusinessApplicantOutput.BusChargeOffNum <>null and BusinessApplicantOutput.BusChargeOffNum <=1 AND BusinessApplicantOutput.BusJudgementLienNum<>null and BusinessApplicantOutput.BusJudgementLienNum<=1) OR
