 if(x5 > 0)then
  result := "5";
 else if(x4 > 0)then
  result := "4";
 else if(x3 > 0)then
  result := "3";
 else if(x2 > 0)then
  result := "2";
 else if(x1 > 0)then
  result := "1";
else if(xX > 0)then
  result := "X";
else if(xA > 0)then
  result := "*";  
 endif endif endif endif endif endif endif 
else if(iMinOrMax = "MIN")then
 if(x1 > 0)then
  result := "1";
 else if( x2 > 0)then
  result := "2";
 else if(x3 > 0)then
  result := "3";
 else if(x4 > 0)then
  result := "4";
 else if(x5 > 0)then
  result := "5";
 endif endif endif endif endif
endif endif

