//AMOUNT
iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT := 0;
if(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_TR_TR = true) then
    if(iNORMALIZED_BUREAU_SEGMENTS.CREDIT_LIMIT_AMT <> null AND Val(iNORMALIZED_BUREAU_SEGMENTS.CREDIT_LIMIT_AMT) > 0)then
             iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT := Val(iNORMALIZED_BUREAU_SEGMENTS.CREDIT_LIMIT_AMT);
    else if(iNORMALIZED_BUREAU_SEGMENTS.HIGH_CRED_AMT <> null AND VAL(iNORMALIZED_BUREAU_SEGMENTS.HIGH_CRED_AMT) > 0)then
             iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT := Val(iNORMALIZED_BUREAU_SEGMENTS.HIGH_CRED_AMT);
    else if((iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_IND_CODE <> null and iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_IND_CODE = "Y") OR 
                              (iNORMALIZED_BUREAU_SEGMENTS.ACCOUNT_TYPE <> null and iNORMALIZED_BUREAU_SEGMENTS.ACCOUNT_TYPE = "YY")) then
            if(iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT <> null AND TRIM(iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT) <> "" ) then 
                  iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT := Val(iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT);
            else 
                  iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT := 0;
            endif
    else
            iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT := 0;
    endif endif endif

else if(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_COLLECT_SEG = true) then
    if(iNORMALIZED_BUREAU_SEGMENTS.ORIGINAL_AMOUNT <> null AND VAL(iNORMALIZED_BUREAU_SEGMENTS.ORIGINAL_AMOUNT) > 0)then
             iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT := Val(iNORMALIZED_BUREAU_SEGMENTS.ORIGINAL_AMOUNT);
    else if(iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT <> null AND TRIM(iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT) <> "" ) then 
                iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT := Max(0, Val(iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT));
    else
          iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT := 0;
    endif endif
endif endif


//VALID-AMOUNT
iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_VALID_AMOUNT := false;
if(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_TR_TR = true) then
    if(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_IND_CODE = "Y" AND iNORMALIZED_BUREAU_SEGMENTS.ACCOUNT_TYPE = "YY") then
             if((iNORMALIZED_BUREAU_SEGMENTS.CREDIT_LIMIT_AMT <> null AND Val(iNORMALIZED_BUREAU_SEGMENTS.CREDIT_LIMIT_AMT) > 0) OR 
                                (iNORMALIZED_BUREAU_SEGMENTS.HIGH_CRED_AMT <> null AND VAL(iNORMALIZED_BUREAU_SEGMENTS.HIGH_CRED_AMT) > 0) OR
                                (iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT <> null AND VAL(iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT) >= 0)) then
                     iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_VALID_AMOUNT := true;
             else
                     iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_VALID_AMOUNT := false;
             endif
    else
             if((iNORMALIZED_BUREAU_SEGMENTS.CREDIT_LIMIT_AMT <> null AND Val(iNORMALIZED_BUREAU_SEGMENTS.CREDIT_LIMIT_AMT) > 0) OR 
                                (iNORMALIZED_BUREAU_SEGMENTS.HIGH_CRED_AMT <> null AND VAL(iNORMALIZED_BUREAU_SEGMENTS.HIGH_CRED_AMT) > 0)) then
                     iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_VALID_AMOUNT := true;
             else
                     iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_VALID_AMOUNT := false;
             endif
    endif

else if(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_COLLECT_SEG = true) then
    if((iNORMALIZED_BUREAU_SEGMENTS.ORIGINAL_AMOUNT <> null AND TRIM(iNORMALIZED_BUREAU_SEGMENTS.ORIGINAL_AMOUNT) <> "") OR 
                             (iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT <> null AND TRIM(iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT) <> "") )then
             iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_VALID_AMOUNT := true;
    else
             iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_VALID_AMOUNT := false;
    endif
endif endif 


//AMONT_PAST_DUE
iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT_PAST_DUE := 0;
if(iNORMALIZED_BUREAU_SEGMENTS.PAST_DUE_AMT <> null AND TRIM(iNORMALIZED_BUREAU_SEGMENTS.PAST_DUE_AMT) <> "")then
     iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT_PAST_DUE  :=  VAL(iNORMALIZED_BUREAU_SEGMENTS.PAST_DUE_AMT);
endif


//BALANCE
iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_BALANCE := 0;
if(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_TR_TR = true) then
        if(iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT <> null AND TRIM(iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT) <> "") then
              if(VAL(iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT) >= iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT_PAST_DUE) then
                      iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_BALANCE := VAL(iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT);
              else
                      iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_BALANCE := iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT_PAST_DUE;
              endif
        else
                 iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_BALANCE := iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT_PAST_DUE;
        endif
else if(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_COLLECT_SEG = true) then
                 iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_BALANCE := VAL(iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT);
endif endif


//AVAILABLE-CREDIT
iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AVAILABLE_CREDIT := 0;
if( (iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT = null OR TRIM(iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT) = "") OR (iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT = 0) ) then
           iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AVAILABLE_CREDIT := 0;
else 
           iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AVAILABLE_CREDIT := MAX(0, (iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT - VAL(iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT)));
endif
if(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AVAILABLE_CREDIT >= 1000000) then
          iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AVAILABLE_CREDIT := 0;
endif


//MONTHLY-PAYMENT
iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_MONTHLY_PAYMENT := 0;
if(iNORMALIZED_BUREAU_SEGMENTS.TERMS_FREQ <> null AND InStringList(iNORMALIZED_BUREAU_SEGMENTS.TERMS_FREQ, "M, P, W, X, Y, S, Q, B, L, E, V", ",")) then 
           iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_MONTHLY_PAYMENT := VAL(iNORMALIZED_BUREAU_SEGMENTS.TERMS_SCHED_MTH_PMT_AMT);
else if (iNORMALIZED_BUREAU_SEGMENTS.PORTFOLIO_TYPE <> null AND iNORMALIZED_BUREAU_SEGMENTS.PORTFOLIO_TYPE = "R") then
           //iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_MONTHLY_PAYMENT := INT(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_BALANCE * 0.0325);
           //Truncate logic removed from filters
           iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_MONTHLY_PAYMENT := iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_BALANCE * 0.0325;
else if(iNORMALIZED_BUREAU_SEGMENTS.TERMS_FREQ <> null AND iNORMALIZED_BUREAU_SEGMENTS.TERMS_PMT_SCHED_MTH = "MIN") then 
           iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_MONTHLY_PAYMENT := VAL(iNORMALIZED_BUREAU_SEGMENTS.TERMS_SCHED_MTH_PMT_AMT);
endif endif endif


//BTL
iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_BTL := 0;
if(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT <> 0 AND iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_BALANCE <> 0) then
        xAmount := iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT;
        xBalance := iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_BALANCE;
        //iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_BTL := INT((xBalance/xAmount)*100);
        //Truncate logic removed from filters
        iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_BTL := (xBalance/xAmount)*100;
else
        iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_BTL := 0;
endif

result:=null;
for every Application.CreditApplication.BusinessApplicant.BusinessApplicantOutput relative to BusinessApplicant do 
	if((BusinessApplicantOutput.BusIsBkInLast7Yrs<>null AND BusinessApplicantOutput.BusIsBkInLast7Yrs="PASS") OR (BusinessApplicantOutput.BusChargeOffNum=0))then
		result:="PASS";
	else if((BusinessApplicantOutput.BusIsBkInLast7Yrs<>null AND BusinessApplicantOutput.BusIsBkInLast7Yrs="FAIL") OR (BusinessApplicantOutput.BusChargeOffNum>0))then
		result:="FAIL";
	endif endif
endfor

