		//ECOA OFAC Flags
        if(BusinessApplicant.OFACFlag<>null and BusinessApplicant.OFACFlag<>"" and BusinessApplicant.OFACFlag="Y")then
             xOFACFlag:=true;
        endif

        if(BusinessApplicantOutput.NegAvailable <> null AND BusinessApplicantOutput.NegAvailable <> "" AND BusinessApplicantOutput.NegAvailable = "Y") then
                xNegFileFlag := true;
        endif

        //Age in years
		if (BusinessApplicantOutput.BusAgeInYears <> null) then
			xBusinessAge := BusinessApplicantOutput.BusAgeInYears;  
		endif

        //Active Trades Count 
		if (BusinessApplicantOutput.BusActiveTradesNum <> null) then
			xActiveTradesCount := BusinessApplicantOutput.BusActiveTradesNum;
		endif

        //Recent Trade Count
		if (BusinessApplicantOutput.BusNumRecentTrades <> null) then
			xRecentTradesCount := BusinessApplicantOutput.BusNumRecentTrades;
		endif

        //Bankruptcy in last 7 years
		if (BusinessApplicantOutput.BusIsBkInLast7Yrs <> null) then
			xBKLast7Years := BusinessApplicantOutput.BusIsBkInLast7Yrs;
		endif

        //Total Chargeoff Count
		if (BusinessApplicantOutput.BusChargeOffNum <> null) then
			xChargeOffCount := BusinessApplicantOutput.BusChargeOffNum;
		endif

        //Judgement and Lien Count
		if (BusinessApplicantOutput.BusJudgementLienNum <> null) then
			xJudgmentAndLienCount := BusinessApplicantOutput.BusJudgementLienNum;
		endif

        //Business Bureau Report available
        xBusinessBureauAvailable := dm_isBusBureauPresent;

		if(BusinessApplicantOutput.businessBureauName <> null) then
			xBureauName := BusinessApplicantOutput.businessBureauName;
		endif

		if(BusinessApplicantOutput.BusRiskScore <> null) then
			xBusinessScore := BusinessApplicantOutput.BusRiskScore;
		endif

