    if( iNORMALIZED_BUREAU_SEGMENTS.PORTFOLIO_TYPE <> null ) then
        if(iNORMALIZED_BUREAU_SEGMENTS.PORTFOLIO_TYPE = "M" ) then
            iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_REAL_PROPERTY := true;

    else if(iNORMALIZED_BUREAU_SEGMENTS.PORTFOLIO_TYPE = "I" AND 
       ( (iNORMALIZED_BUREAU_SEGMENTS.ACCOUNT_TYPE <> null AND InStringList(iNORMALIZED_BUREAU_SEGMENTS.ACCOUNT_TYPE, "CV, FI, FH, FR, HE, HI, MB, PI, RE, RL, RM, RT, SI, SM, VA, VM", ",")) OR
        (iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE <> null AND InStringList(iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE, "FHA, FHI, HEQ, VAL", ","))    )) then


      iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_REAL_PROPERTY := true;
 endif endif endif endif



 //COLL-EXT
 iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_COLL_EXT := false;
 if(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_COLLECT_SEG OR 
         (iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_TR_TR AND iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_IND_CODE = "Y")) then

           iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_COLL_EXT := true;
 endif


 //INSTALL
 iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_INSTALL := false;
 if(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_TR_TR = true) then
         if((iNORMALIZED_BUREAU_SEGMENTS.PORTFOLIO_TYPE <> null AND iNORMALIZED_BUREAU_SEGMENTS.PORTFOLIO_TYPE = "I") AND
                      (iNORMALIZED_BUREAU_SEGMENTS.ACCOUNT_TYPE <> null AND NOT InStringList(iNORMALIZED_BUREAU_SEGMENTS.ACCOUNT_TYPE, "CV, FI, FH, FR, HE, HI, MB, PI, RE, RL, RM, RT, SI, SM, VA, VM", ",")) AND
                      (iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE <> null AND NOT InStringList(iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE, "FHA, FHI, HEQ, VAL", ",")) AND
                      iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_COLL_EXT = false) then

           iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_INSTALL := true;

 endif endif


 //COLLECTION
 iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_COLLECTION := false;
 if((iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_IND_CODE = "Y") OR 
       (iNORMALIZED_BUREAU_SEGMENTS.MOP <> null AND InStringList(iNORMALIZED_BUREAU_SEGMENTS.MOP, "9B, 9P", ",") )    OR
       (iNORMALIZED_BUREAU_SEGMENTS.MAX_DELINQ_MOP <> null AND InStringList(iNORMALIZED_BUREAU_SEGMENTS.MAX_DELINQ_MOP, "9B, 9P", ",") )   OR
       (iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE <> null AND InStringList(iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE, "AOA, BDC, CCC, CLA, COL, CPM, CPP, PCL", ",") )    OR
       iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_COLLECT_SEG = true) then
    iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_COLLECTION := true;
 endif



//COLL-INT
