         (iNORMALIZED_BUREAU_SEGMENTS.PORTFOLIO_TYPE <> null and iNORMALIZED_BUREAU_SEGMENTS.PORTFOLIO_TYPE = "M" AND dm_isBlankOrZero(iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT) AND dm_isBlankOrZero(iNORMALIZED_BUREAU_SEGMENTS.PAST_DUE_AMT) AND iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE <> null and InStringList(Trim(iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE), "PPC, PPD", ","))   OR
         (iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_IND_CODE <> null and iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_IND_CODE = "Y" and (iNORMALIZED_BUREAU_SEGMENTS.DATE_PAID_OUT = null or TRIM(iNORMALIZED_BUREAU_SEGMENTS.DATE_PAID_OUT) <> ""))     )) then

                 iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_PAID := true;

else if(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_COLLECT_SEG AND 
       ((iNORMALIZED_BUREAU_SEGMENTS.DATE_PAID_OUT <> null and TRIM(iNORMALIZED_BUREAU_SEGMENTS.DATE_PAID_OUT) <> "") OR (iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE <> null and iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE = "PCL") )      ) then

