if(iDate <> null and Trim(iDate) <> "") then
    xLength := strlen(Trim(iDate));
endif

if (iDate = null or Trim(iDate) = "" or iDate = "00000000") then
  xDay  := "01";
   xMonth := "01";
   xYear := "1900" ;

 else if(xLength = 8 and iDate>"19000000") then       //Assume yyyymmdd
     xDay := Right (iDate, 2);
     xMonth := Mid(iDate,5,2);
     xYear := Left (iDate,4); 

else if(xLength = 8) then       //Assume mmddyyyy
     xDay := Mid (iDate, 3,2);
     xMonth := Left(iDate,2);
     xYear := Right (iDate,4);

 else if(xLength = 6 ) then   //Assume mmyyyy
     xDay := "01";
     xMonth := Left(iDate,2);
     xYear := Right (iDate,4);

else if(xLength = 10 and (Instr("-", iDate, true)<>0)) then   //Assume yyyy-mm-dd
     xDay := Right (iDate, 2);
     xMonth := Mid(iDate,6,2);
     xYear := Left (iDate,4);

else if(xLength = 10 ) then   //Assume mm/dd/yyyy
       xDay := Mid (iDate, 4,2);
       xMonth := Left(iDate,2);
       xYear := Right (iDate,4);

     else if(xLength = 7 ) then   //Assume mm/yyyy
       xDay := "01";
       xMonth := Left(iDate,2);
       xYear := Right (iDate,4);

 endif endif  endif    endif    endif    endif    endif


 result := TimestampfromStr(xYear + "-" + xMonth + "-" + xDay);

result := null;

