  //if PG app then do not qualify for upgrade
		  if(CreditApplication.PG_APP <> null and CreditApplication.PG_APP ="Y") then
		  	  xCallNeustar := "N";
			endif

if(InStringList(CreditApplication.T_CHANNEL_CODE,"213,220,240,260",",") ) then
