   if(iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_IND_CODE <> null AND iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_IND_CODE = "Q") then
      if(iNORMALIZED_BUREAU_SEGMENTS.ACCOUNT_TYPE <> null AND InStringList(iNORMALIZED_BUREAU_SEGMENTS.ACCOUNT_TYPE, "AL, AR, AT, AU", ",")) then
            iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_CAP := true;
      else if (((iNORMALIZED_BUREAU_SEGMENTS.ACCOUNT_TYPE <> null AND iNORMALIZED_BUREAU_SEGMENTS.ACCOUNT_TYPE = "LE") OR 
                (iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE <> null AND InStringList(iNORMALIZED_BUREAU_SEGMENTS.REMARK_CODE, "LBR, LEA, PLL", ","))) AND
                iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_AMOUNT >= 5000) then
            iNORMALIZED_BUREAU_SEGMENTS.STAGG_FF_CAP := true;
