                             ((iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT <> null AND TRIM(iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT) <> "" AND VAL(iNORMALIZED_BUREAU_SEGMENTS.CUR_BAL_AMT) >= 0) OR 
